START TRANSACTION;
DROP TABLE IF EXISTS sqlite_stat3;
CREATE TABLE IF NOT EXISTS sqlite_stat3 (
 tbl TEXT,
 idx TEXT,
 neq TEXT,
 nlt TEXT,
 ndlt TEXT,
 sample TEXT
);
DROP TABLE IF EXISTS sqlite_vs_properties;
CREATE TABLE IF NOT EXISTS sqlite_vs_properties (
 parentType TEXT,
 parentName TEXT,
 propertyName TEXT,
 propertyValue TEXT
);
DROP TABLE IF EXISTS sqlite_vs_links_names;
CREATE TABLE IF NOT EXISTS sqlite_vs_links_names (
 name TEXT,
 alias TEXT
);
DROP TABLE IF EXISTS sqlite_vsp_diagrams;
CREATE TABLE IF NOT EXISTS sqlite_vsp_diagrams (
 name TEXT,
 diadata TEXT,
 comment TEXT,
 preview TEXT
);
DROP TABLE IF EXISTS sqlite_sp_functions;
CREATE TABLE IF NOT EXISTS sqlite_sp_functions (
 name TEXT,
 text TEXT
);
DROP TABLE IF EXISTS C_INDEX;
CREATE TABLE IF NOT EXISTS C_INDEX (
 INDEX_ID int NOT NULL,
 VOCA_ID text,
 DAY_NO int NOT NULL,
 PREFIX_GRP int,
 PREFIX_ORD int,
 VOCABULARY text,
 MEANING_INDEX text,
 VOCA_CAT int,
 PRIMARY KEY(INDEX_ID)
);
DROP TABLE IF EXISTS C_VOCA;
CREATE TABLE IF NOT EXISTS C_VOCA (
 VOCA_ID integer NOT NULL,
 DAY_NO integer NOT NULL,
 PREFIX_GRP integer,
 PREFIX_ORD integer,
 VOCABULARY varchar(300),
 VOCABULARY_TAG varchar(3000),
 MEANING_TAG varchar(3000),
 MEANING_INDEX varchar(300),
 ORIGIN_EXP_TAG varchar(3000),
 ORIGIN_APPENDIX varchar(300),
 DERIVATIVE_TAG varchar(3000),
 SENTENCE_TAG varchar(3000),
 MEANING_QUICK varchar(300),
 SOUND_ORD INTEGER DEFAULT 0,
 PRIMARY KEY(VOCA_ID)
);
DROP TABLE IF EXISTS C_TEST;
CREATE TABLE IF NOT EXISTS C_TEST (
 TEST_ID INTEGER NOT NULL,
 DAY_NO INTEGER,
 VOCA_ID INTEGER,
 QUE_EN VARCHAR(300),
 ANS_EN1 VARCHAR(300),
 ANS_EN2 VARCHAR(300),
 ANS_EN3 VARCHAR(300),
 QUE_KO VARCHAR(300),
 ANS_KO1 VARCHAR(300),
 ANS_KO2 VARCHAR(300),
 ANS_KO3 VARCHAR(300),
 TEST_GRP INTEGER DEFAULT (0),
 PRIMARY KEY(TEST_ID)
);
DROP TABLE IF EXISTS C_QUIZ;
CREATE TABLE IF NOT EXISTS C_QUIZ (
 QUIZ_ID INTEGER NOT NULL PRIMARY KEY,
 DAY_NO INTEGER NOT NULL,
 QUIZ_CNT INTEGER NOT NULL DEFAULT 0,
 CORRECT_CNT INTEGER NOT NULL DEFAULT 0,
 TEST_GRP INTEGER DEFAULT 0
);
DROP TABLE IF EXISTS C_MYVOCA;
CREATE TABLE IF NOT EXISTS C_MYVOCA (
 MYVOCA_ID integer PRIMARY KEY,
 VOCA_ID integer NOT NULL
);
DROP TABLE IF EXISTS C_MEMORY;
CREATE TABLE IF NOT EXISTS C_MEMORY (
 MEMORY_ID INTEGER NOT NULL PRIMARY KEY,
 VOCA_ID INTEGER NOT NULL
);
DROP INDEX IF EXISTS INDEX_C_VOCA_1;
CREATE INDEX IF NOT EXISTS INDEX_C_VOCA_1 ON C_VOCA (
 DAY_NO ASC,
 PREFIX_GRP ASC,
 PREFIX_ORD ASC
);
DROP INDEX IF EXISTS INDEX_C_INDEX_1;
CREATE INDEX IF NOT EXISTS INDEX_C_INDEX_1 ON C_INDEX (
 DAY_NO ASC,
 PREFIX_GRP ASC,
 PREFIX_ORD ASC
);
COMMIT;
